document.addEventListener("DOMContentLoaded", function () {
const currentLocation = window.location.search;
//const sURL = currentLocation.replace("?live&url=", ""); 
//const cHst = atob("Y29ycy1wcm94eS5jb29rcy5meWk=");
//const ipTv = "nk.cdnnet.xyz:80/play/live.php?mac=00:1A:79:B5:46:30&stream=";

function getSecureUrl(url, corsProxy = "https://cors-proxy.cooks.fyi") {
  try {
    const parsed = new URL(url);
    if (parsed.protocol === "https:") {
      return url;
    } else if (parsed.protocol === "http:") {
     
      const proxy = corsProxy.endsWith("/") ? corsProxy : corsProxy + "/";
      return proxy + url;
    }
    // If not http or https, return as is or handle as needed
    return url;
  } catch (e) {
    // Invalid URL
    return url;
  }
}
const sURL = currentLocation.replace("?live&url=", ""); 
   const vLink = getSecureUrl(sURL);  
  
  var tsPlayer = null,
      hlsPlayer = null,
      dashPlayer = null;

  var stopPlayers = function () {
    if (tsPlayer) {
      tsPlayer.destroy();
      tsPlayer = null;
    }

    if (hlsPlayer) {
      hlsPlayer.destroy();
      hlsPlayer = null;
    }

    if (dashPlayer) {
      dashPlayer.destroy();
      dashPlayer = null;
    }
  };

  var hide_for_error = function () {
    $("#player").hide();
    stopPlayers();
  };

  var show_for_ok = function () {
    $("#player").show();
  };

  // Start play HTTP-TS.
  if (!window.mpegts) {
    mpegts.LoggingControl.applyConfig({
      enableDebug: false,
      enableVerbose: false,
      enableInfo: false,
      enableWarn: false,
      enableError: true
    });
    hide_for_error();
    return;
  }

  show_for_ok();
//https://" + cHst + "/" + "http://" + ipTv + sURL,
  tsPlayer = videojs("#player");
  tsPlayer.src({
    src: vLink,
    type: "video/mp2t",
    suppressNotSupportedError: true,
    mediaDataSource: {
      type: "mpegts",
      isLive: true,
      cors: true,
      withCredentials: false
    },
    config: {
      enableWorker: true,
      enableWorkerForMSE: true
    }
  });

  tsPlayer.load();
  tsPlayer.play();
  //return;
  //}

  // Start play HLS.
  if (sURL.indexOf(".m3u8") > 0) {
    show_for_ok();

    hlsPlayer = new videojs("#player");
    hlsPlayer.src({
      type: "application/x-mpegURL",
      src: vLink
    });
    hlsPlayer.load();
    hlsPlayer.play();
    return;
  }

  // Start play MPEG-DASH.
  if (sURL.indexOf(".mpd") > 0) {
    show_for_ok();

    dashPlayer = new videojs("#player");
    dashPlayer.src({
      type: "application/dash+xml",
      src: vLink
    });
    dashPlayer.load();
    dashPlayer.play();
    return;
  }
});


